#pragma once#include <oci.h>class NstvConnection{protected:	//是否连接上	bool connected;public:	//与连接相关的句柄	OCIEnv*			envhp;      //环境句柄	OCIError*		errhp;		//错误句柄	OCIServer*		srvhp;		//服务句柄	OCISvcCtx*		svchp;		//服务上下文句柄	OCISession*		sessionhp;	//用户会话句柄	OCIDescribe*	dschp;		//描述句柄	OCITrans*		tranhp;		//事务句柄public:		//构造函数	NstvConnection();	//析构	~NstvConnection() { disconnect(); }	//建立与Oracle的连接	bool connect(const char* dblink, const char* username, const char* password);	//断连	void disconnect();	//是否连接	bool isConnected() { return connected; }};